package Basic;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("before swapping");
int a=100;
int b=500;
int T;
System.out.println(+a);
System.out.println(+b);
T=a;
a=b;
b=T;
System.out.println("after swapping");
System.out.println(+a);
System.out.println(+b);

	}

}
